<template>
    <div class="ratio-box"
        v-bind:class="computedCssClasses">
        <div class="ratio-content">
            <slot></slot>
        </div>
    </div>
</template>

<script>
    let ratioSupports = ['1:1', '2:1', '3:1', '3:2', '4:3', '6:4', '16:9'];
    export default {
        name: 'nftn-moxie-container',
        props: {
            ratio: {
                type: String,
                default: '1:1'
            }
        },
        computed: {
            computedCssClasses () {
                let classes = [];
                let ratio = this.ratio;
                if (ratioSupports.indexOf(ratio) === -1) {
                    classes.push('ratio-1_1');
                } else {
                    classes.push('ratio-' + ratio.replace(':', '_'));
                }
                return classes;
            }
        }
    };
</script>

<style lang="less">
    .ratio-box {
        position: relative;
        &:before {
            display: block;
            content: "";
            width: 100%;
        }

        & > .ratio-content {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background-color: #eeeeee;
        }

        & > .ratio-content span:before {
            content: "";
        }
        & > .ratio-content span:after {
            content: "";
        }
    }

    .ratio-1_1:before {
        padding-top: 100%;
    }

    .ratio-2_1:before {
        padding-top: (1 / 2) * 100%;
    }

    .ratio-3_1:before {
        padding-top: (1 / 3) * 100%;
    }

    .ratio-3_2:before {
        padding-top: (2 / 3) * 100%;
    }

    .ratio-4_3:before {
        padding-top: (3 / 4) * 100%;
    }

    .ratio-6_4:before {
        padding-top: (4 / 6) * 100%;
    }

    .ratio-16_9:before {
        padding-top: (9 / 16) * 100%;
    }
</style>
